    };
    console.log(result);
    console.log(startArr);

    return (
        <div className="App">
            <h1>Gallows Game</h1>
            <div className="img-box"></div>
            <WordByLetter word={word} letterArr={arr} />
            <button>Get new word</button>
            <button onClick={arrayHandler}>Test click</button>
            <Alphabet />
        </div>
    );
}

export default App;
