import { useState } from "react";
import "./App.css";
import words from "./data/words";
import Alphabet from "./components/Alphabet";
import WordByLetter from "./components/WordByLetter";

function App() {
    function getRandomInt(max) {
        return Math.floor(Math.random() * max);
    }
    const id = getRandomInt(words.length);
    const newWord = words[id].split("");

    const [word, setWord] = useState(newWord);
    const setWordHandler = () => {
        setWord(words[getRandomInt(words.length)].split(""));
    };
    console.log(word);

    const startArray = new Array(word.length).fill("");
    console.log(startArray);

    const [arrAddLett, setArrAddLett] = useState(startArray);
    console.log(arrAddLett);
    const arrayAddHandler = (argLetter = "a") => {
        const intermArr = word.map((el) => {
            if (el === argLetter) {
                return argLetter;
            } else {
                return " ";
            }
        });
        console.log(intermArr);
    };

    return (
        <div className="App">
            <h1>Gallows Game</h1>
            <div className="img-box"></div>
            <WordByLetter word={word} />
            <button onClick={setWordHandler}>Get new word</button>
            <button onClick={arrayAddHandler}>Test click</button>
            <Alphabet />
        </div>
    );
}

export default App;
