import { useState } from "react";
import "./App.css";
import words from "./data/words";
import Alphabet from "./components/Alphabet";
import WordByLetter from "./components/WordByLetter";

function App() {
    function getRandomInt(max) {
        return Math.floor(Math.random() * max);
    }
    const id = getRandomInt(words.length);
    const newWord = words[id].split("");

    const [word, setWord] = useState(newWord);
    // const [classesList, setClassesList] = useState("word-by-letter");

    const collorChange = (el) => {
        const letterSearch = word
            .filter((letter) => letter === el)
            .map((lett) => ({ value: lett, className: "green-letter" }));
        console.log(letterSearch);
        return letterSearch;
    };
    const result = collorChange("a");

    return (
        <div className="App">
            <h1>Gallows Game</h1>
            <div className="img-box"></div>
            <WordByLetter word={word} />
            <div>
                {result.map((el) => (
                    <div>{el}</div>
                ))}
            </div>
            <button>Get new word</button>
            <button>Test click</button>
            <Alphabet />
        </div>
    );
}

export default App;
