import { useState } from "react";
import "./App.css";
import words from "./data/words";
import Alphabet from "./components/Alphabet";
import WordByLetter from "./components/WordByLetter";

function App() {
    function getRandomInt(max) {
        return Math.floor(Math.random() * max);
    }
    const id = getRandomInt(words.length);
    const newWord = words[id].split("");

    const [word, setWord] = useState(newWord);
    const setWordHandler = () => {
        setWord(words[getRandomInt(words.length)].split("_"));
    };
    console.log(word);

    const startArray = new Array(word.length).fill("_");

    const [arrAddLett, setArrAddLett] = useState(startArray);
    console.log(arrAddLett);

    const arrayAddHandler = (argLetter) => {
        const intermArr = [];
        word.forEach((el, index) => {
            if (el === argLetter) {
                intermArr[index] = argLetter;
            } else {
                if (intermArr[index] !== "_") {
                    intermArr[index] = el;
                }
            }
        });
        setArrAddLett(intermArr);
    };

    return (
        <div className="App">
            <h1>Gallows Game</h1>
            <div className="img-box"></div>
            <WordByLetter word={arrAddLett} />
            <button onClick={setWordHandler}>Get new word</button>
            <button>Test click</button>
            <Alphabet arrayAdd={arrayAddHandler} />
        </div>
    );
}

export default App;
