import words from "../data/words";

function RandomWord() {
    function getRandomInt(max) {
        return Math.floor(Math.random() * max);
    }
    const id = getRandomInt(words.length);

    const letterArray = [];

    function createWordByLetter() {
        for (const wordByLetter of words[id]) {
            letterArray.push(wordByLetter);
        }
    }
    createWordByLetter();
    console.log(letterArray);
    //return <div className="word">{words[id]}</div>;

    return (
        <div className="word">
            {letterArray.map((byLetter) => {
                return (
                    <span key={new Date.now()} className="word-by-letter">
                        {byLetter}
                    </span>
                );
            })}
        </div>
    );
}

export default RandomWord;
